# Nombre de handle l' array 
def getNumber(list)
    puts "Il y a  #{list.length} Handle "
    puts "****************\n"
end

# Le handle le plus court la  liste 
def getShorter(list)
    shorterItem = nil
    shorterItemAccount = nil
    list.each do |i|
        lengthI = i.length
        if shorterItem == nil || lengthI < shorterItem
            shorterItem = lengthI
            shorterItemAccount = i
        end
    end
    puts "Le Handle le plus court est #{shorterItemAccount}  avec #{shorterItem} caractères"
    puts "****************\n"
end

# Handles contenant 5 caractères sachant que le @ ne compte pas pour 1 caractère
def get5char(list)
    number = 0
    list.each do |i|
        if i.slice!(0) == "@"
            i = i.slice!(1, i.length)
        end
        if i.length == 5
            number += 1
        end
    end
    puts "Nombre de handles de 5 caractères => #{number}"
    puts "****************\n"
end

# Commence par une majuscule juste après le @
def getUppercase(list)
    number = 0
    list.each do |i|
        if i.slice!(0) == "@"
            i = i.slice!(1, i.length)
        end
        if i[0] =~ /[A-Z]/
            number += 1
        end
    end
    puts "Il y a  #{number}  commençant par une majuscule"
    puts "****************\n"
end

# Mis dans l'ordre alphabétique.
def getAlphaOrder(list)
    puts "mis dans l' ordre alphabétique :"
    puts list = list.sort
    puts "****************\n"
end

# Ranger daans l'ordre croissant par taille des handles
def getSizeSort(list)
    puts "Ranger dasn l'ordre croissant par taille :"
    puts list = list.sort_by! { |n| n.size }
    puts "****************\n"
end

# La position de @epenser dans l'array 
def getValuePosition(list, string)
    filterList = list.select { |n| n == string }
    positionList = list.find_index { |n| n == string }
    puts "#{filterList} est à la posotion #{positionList} dans l' array"
    puts "****************\n"
end

# Tri par taille des Handles (par 1 caractère , par 2 ..etc..)
def getCharachterSize(list)
    sortList = list.sort_by! { |n| n.length }
    puts "Tri par taille :"
    puts sortList
    puts"****************\n"
end

def perform
    getNumber(handleTwitter)
    getShorter(handleTwitter)
    get5char(handleTwitter)
    getUppercase(handleTwitter)
    getAlphaOrder(handleTwitter)
    getSizeSort(handleTwitter)
    getValuePosition(handleTwitter, "@epenser")
    getCharachterSize(handleTwitter)
end
perform

